package JavaStrings;

public class SeperatCharactersFromString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str="CHARACTERS";
		
		
		char[] ch=str.toCharArray();
		
		for(int i=0;i<ch.length;i++) {
		System.out.print(ch[i] + " ");
		}
	}

}
